mapper.select:
select *
from user_completion_question
where userid = #{userID} and questionnaireid = #{questionnaireID} and question_contentid = #{questionContentID}

mapper.update:
update user_completion_question
set completion_content = #{completionContent}
where userid = #{userID} and questionnaireid = #{questionnaireID} and question_contentid = #{questionContentID}
